import React, { useState, useEffect } from 'react';
import './QuizBuilder.module.css';

const QuizBuilder = () => {
  const [config, setConfig] = useState({
    num_questions: 10,
    num_choices: 4,
    shuffle: true,
    filters: {
      categories: [],
      age_groups: [],
      complexities: [],
      difficulty_tiers: [],
      clinical_specifiers: [],
      course_specifiers: [],
      symptom_variants: [],
      specifics: [],
      exclude_specifics: []
    },
    quiz_settings: {
      time_limit: 120,
      show_feedback: true,
      show_explanations: true,
      allow_review: true,
      passing_score: 70
    }
  });

  const [preview, setPreview] = useState(null);
  const [loading, setLoading] = useState(false);

  const categories = [
    'Neurodevelopmental Disorders',
    'Schizophrenia Spectrum and Other Psychotic Disorders',
    'Bipolar and Related Disorders',
    'Depressive Disorders',
    'Anxiety Disorders',
    'Obsessive-Compulsive and Related Disorders',
    'Trauma- and Stressor-Related Disorders',
    'Dissociative Disorders',
    'Somatic Symptom and Related Disorders',
    'Feeding and Eating Disorders',
    'Elimination Disorders',
    'Sleep-Wake Disorders',
    'Sexual Dysfunctions',
    'Gender Dysphoria',
    'Disruptive, Impulse-Control, and Conduct Disorders',
    'Substance-Related and Addictive Disorders',
    'Neurocognitive Disorders',
    'Personality Disorders',
    'Paraphilic Disorders'
  ];

  const ageGroups = ['child', 'adult'];
  const complexities = ['easy', 'moderate', 'high'];
  const difficultyTiers = ['beginner', 'intermediate', 'advanced', 'expert'];
  const clinicalSpecifiers = [
    'Mild', 'Moderate', 'Severe', 'Mild to Moderate', 'Moderate to Severe',
    'Recurrent', 'Chronic', 'Acute', 'In Partial Remission', 'In Full Remission',
    'With Psychotic Features', 'Without Psychotic Features', 'With Catatonic Features',
    'With Peripartum Onset', 'With Seasonal Pattern', 'With Atypical Features'
  ];
  const courseSpecifiers = [
    'First Episode', 'Multiple Episodes', 'Continuous', 'Episodic',
    'Rapid Cycling', 'With Interepisode Recovery'
  ];

  const handleFilterChange = (filterType, value, checked) => {
    setConfig(prev => ({
      ...prev,
      filters: {
        ...prev.filters,
        [filterType]: checked
          ? [...prev.filters[filterType], value]
          : prev.filters[filterType].filter(item => item !== value)
      }
    }));
  };

  const handleInputChange = (field, value) => {
    setConfig(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleNestedInputChange = (parent, field, value) => {
    setConfig(prev => ({
      ...prev,
      [parent]: {
        ...prev[parent],
        [field]: value
      }
    }));
  };

  const generatePreview = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/quiz/preview', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(config)
      });
      const data = await response.json();
      setPreview(data);
    } catch (error) {
      console.error('Preview generation failed:', error);
    }
    setLoading(false);
  };

  const saveTemplate = () => {
    const templateName = prompt('Enter template name:');
    if (templateName) {
      localStorage.setItem(`quiz_template_${templateName}`, JSON.stringify(config));
      alert('Template saved!');
    }
  };

  const loadTemplate = () => {
    const templates = Object.keys(localStorage).filter(key => key.startsWith('quiz_template_'));
    if (templates.length === 0) {
      alert('No saved templates found.');
      return;
    }

    const templateName = templates[0].replace('quiz_template_', '');
    const savedConfig = JSON.parse(localStorage.getItem(templates[0]));
    setConfig(savedConfig);
    alert(`Template "${templateName}" loaded!`);
  };

  return (
    <div className="quiz-builder">
      <h1>Quiz Builder</h1>

      <div className="builder-layout">
        <div className="config-panel">
          <h2>Quiz Configuration</h2>

          {/* Basic Settings */}
          <div className="config-section">
            <h3>Basic Settings</h3>
            <label>
              Number of Questions:
              <input
                type="number"
                value={config.num_questions}
                onChange={(e) => handleInputChange('num_questions', parseInt(e.target.value))}
                min="1"
                max="50"
              />
            </label>
            <label>
              Number of Choices:
              <input
                type="number"
                value={config.num_choices}
                onChange={(e) => handleInputChange('num_choices', parseInt(e.target.value))}
                min="2"
                max="6"
              />
            </label>
            <label>
              <input
                type="checkbox"
                checked={config.shuffle}
                onChange={(e) => handleInputChange('shuffle', e.target.checked)}
              />
              Shuffle Answer Choices
            </label>
          </div>

          {/* Filters */}
          <div className="config-section">
            <h3>Filters</h3>

            <div className="filter-group">
              <h4>Categories</h4>
              <div className="checkbox-grid">
                {categories.map(category => (
                  <label key={category}>
                    <input
                      type="checkbox"
                      checked={config.filters.categories.includes(category)}
                      onChange={(e) => handleFilterChange('categories', category, e.target.checked)}
                    />
                    {category}
                  </label>
                ))}
              </div>
            </div>

            <div className="filter-group">
              <h4>Age Groups</h4>
              {ageGroups.map(age => (
                <label key={age}>
                  <input
                    type="checkbox"
                    checked={config.filters.age_groups.includes(age)}
                    onChange={(e) => handleFilterChange('age_groups', age, e.target.checked)}
                  />
                  {age}
                </label>
              ))}
            </div>

            <div className="filter-group">
              <h4>Complexity</h4>
              {complexities.map(complexity => (
                <label key={complexity}>
                  <input
                    type="checkbox"
                    checked={config.filters.complexities.includes(complexity)}
                    onChange={(e) => handleFilterChange('complexities', complexity, e.target.checked)}
                  />
                  {complexity}
                </label>
              ))}
            </div>

            <div className="filter-group">
              <h4>Difficulty Tiers</h4>
              {difficultyTiers.map(tier => (
                <label key={tier}>
                  <input
                    type="checkbox"
                    checked={config.filters.difficulty_tiers.includes(tier)}
                    onChange={(e) => handleFilterChange('difficulty_tiers', tier, e.target.checked)}
                  />
                  {tier}
                </label>
              ))}
            </div>

            <div className="filter-group">
              <h4>Clinical Specifiers</h4>
              <div className="checkbox-grid">
                {clinicalSpecifiers.map(specifier => (
                  <label key={specifier}>
                    <input
                      type="checkbox"
                      checked={config.filters.clinical_specifiers.includes(specifier)}
                      onChange={(e) => handleFilterChange('clinical_specifiers', specifier, e.target.checked)}
                    />
                    {specifier}
                  </label>
                ))}
              </div>
            </div>

            <div className="filter-group">
              <h4>Course Specifiers</h4>
              {courseSpecifiers.map(specifier => (
                <label key={specifier}>
                  <input
                    type="checkbox"
                    checked={config.filters.course_specifiers.includes(specifier)}
                    onChange={(e) => handleFilterChange('course_specifiers', specifier, e.target.checked)}
                  />
                  {specifier}
                </label>
              ))}
            </div>
          </div>

          {/* Quiz Settings */}
          <div className="config-section">
            <h3>Quiz Settings</h3>
            <label>
              Time Limit per Question (seconds):
              <input
                type="number"
                value={config.quiz_settings.time_limit}
                onChange={(e) => handleNestedInputChange('quiz_settings', 'time_limit', parseInt(e.target.value))}
                min="30"
                max="600"
              />
            </label>
            <label>
              <input
                type="checkbox"
                checked={config.quiz_settings.show_feedback}
                onChange={(e) => handleNestedInputChange('quiz_settings', 'show_feedback', e.target.checked)}
              />
              Show Immediate Feedback
            </label>
            <label>
              <input
                type="checkbox"
                checked={config.quiz_settings.show_explanations}
                onChange={(e) => handleNestedInputChange('quiz_settings', 'show_explanations', e.target.checked)}
              />
              Show Detailed Explanations
            </label>
            <label>
              <input
                type="checkbox"
                checked={config.quiz_settings.allow_review}
                onChange={(e) => handleNestedInputChange('quiz_settings', 'allow_review', e.target.checked)}
              />
              Allow Answer Review
            </label>
            <label>
              Passing Score (%):
              <input
                type="number"
                value={config.quiz_settings.passing_score}
                onChange={(e) => handleNestedInputChange('quiz_settings', 'passing_score', parseInt(e.target.value))}
                min="0"
                max="100"
              />
            </label>
          </div>
        </div>

        <div className="preview-panel">
          <h2>Quiz Preview</h2>
          <button onClick={generatePreview} disabled={loading}>
            {loading ? 'Generating...' : 'Generate Preview'}
          </button>

          {preview && (
            <div className="preview-content">
              <h3>Preview Results</h3>
              <p>Questions: {preview.total_questions}</p>
              <p>Estimated Time: {preview.estimated_time} minutes</p>
              <p>Difficulty Distribution: {JSON.stringify(preview.difficulty_distribution)}</p>
              <p>Categories: {preview.categories.join(', ')}</p>
            </div>
          )}

          <div className="template-actions">
            <button onClick={saveTemplate}>Save Template</button>
            <button onClick={loadTemplate}>Load Template</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuizBuilder;
