import React, { useState } from 'react';
import styles from '../styles/QuizPlayer.module.css';

const QuestionDisplay = ({ 
  question, 
  onAnswerSelect, 
  selectedAnswer, 
  showFeedback = false,
  isSubmitted = false 
}) => {
  const [hoveredOption, setHoveredOption] = useState(null);

  if (!question) {
    return (
      <div className={styles.loading}>
        <div className={styles.spinner}></div>
      </div>
    );
  }

  const handleOptionClick = (optionId) => {
    if (!isSubmitted && onAnswerSelect) {
      onAnswerSelect(optionId);
    }
  };

  const getOptionClassName = (optionId) => {
    let className = styles.answerOption;

    if (selectedAnswer === optionId && !isSubmitted) {
      className += ` ${styles.selected}`;
    }

    if (isSubmitted) {
      if (optionId === question.correct_answer) {
        className += ` ${styles.correct}`;
      } else if (selectedAnswer === optionId && optionId !== question.correct_answer) {
        className += ` ${styles.incorrect}`;
      }
    }

    if (hoveredOption === optionId && !isSubmitted) {
      className += ` ${styles.hovered}`;
    }

    return className;
  };

  const renderCaseDetails = () => {
    if (!question.case_details) return null;

    return (
      <div className={styles.caseDetails}>
        <h4>Case Details</h4>
        {question.case_details.age && (
          <p><strong>Age:</strong> {question.case_details.age}</p>
        )}
        {question.case_details.gender && (
          <p><strong>Gender:</strong> {question.case_details.gender}</p>
        )}
        {question.case_details.chief_complaint && (
          <p><strong>Chief Complaint:</strong> {question.case_details.chief_complaint}</p>
        )}
        {question.case_details.history && (
          <p><strong>History:</strong> {question.case_details.history}</p>
        )}
        {question.case_details.examination && (
          <p><strong>Examination:</strong> {question.case_details.examination}</p>
        )}
        {question.case_details.vitals && (
          <p><strong>Vitals:</strong> {question.case_details.vitals}</p>
        )}
      </div>
    );
  };

  const renderOptions = () => {
    if (!question.options || !Array.isArray(question.options)) {
      return <p>No options available</p>;
    }

    return question.options.map((option, index) => (
      <button
        key={option.id || index}
        className={getOptionClassName(option.id || index)}
        onClick={() => handleOptionClick(option.id || index)}
        onMouseEnter={() => setHoveredOption(option.id || index)}
        onMouseLeave={() => setHoveredOption(null)}
        disabled={isSubmitted}
      >
        <span className={styles.answerLabel}>
          {String.fromCharCode(65 + index)}.
        </span>
        {option.text || option}
      </button>
    ));
  };

  const renderFeedback = () => {
    if (!showFeedback || !isSubmitted) return null;

    const isCorrect = selectedAnswer === question.correct_answer;

    return (
      <div className={`${styles.feedback} ${isCorrect ? styles.correct : styles.incorrect}`}>
        <h4>{isCorrect ? '✓ Correct!' : '✗ Incorrect'}</h4>
        {question.explanation && (
          <p><strong>Explanation:</strong> {question.explanation}</p>
        )}
        {question.reference && (
          <p><strong>Reference:</strong> {question.reference}</p>
        )}
        {!isCorrect && (
          <p><strong>Correct Answer:</strong> {
            question.options?.find(opt => 
              (opt.id || question.options.indexOf(opt)) === question.correct_answer
            )?.text || question.correct_answer
          }</p>
        )}
      </div>
    );
  };

  return (
    <div className={styles.questionContainer}>
      <div className={styles.questionHeader}>
        <div className={styles.questionNumber}>
          Question {question.question_number || 'N/A'}
        </div>
        <h2 className={styles.questionText}>
          {question.question_text || question.text}
        </h2>
        {question.difficulty && (
          <div className={styles.difficultyBadge}>
            Difficulty: {question.difficulty}
          </div>
        )}
        {question.category && (
          <div className={styles.categoryBadge}>
            {question.category}
          </div>
        )}
      </div>

      {renderCaseDetails()}

      <div className={styles.answersContainer}>
        {renderOptions()}
      </div>

      {renderFeedback()}
    </div>
  );
};

export default QuestionDisplay;
